import { useState } from "react";
import "./Register.scss";

const Register = () => {
  const [input, setInput] = useState({
    username: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  const inputs = [
    {
      id: 1,
      name: "username",
      type: "text",
      placeholder: "Enter Username",
      errorMessage: "User Name must be between 3-16 characters and should'nt include any special character",
      label: "Username",
    },
    {
      id: 2,
      name: "email",
      type: "email",
      placeholder: "Enter Email",
      errorMessage: "Please provide valid email",
      label: "Email",
    },
    {
      id: 3,
      name: "Password",
      type: "password",
      placeholder: "Enter Password",
      errorMessage: "Password must be between 6-20 characters",
      label: "Password",
    },
    {
      id: 4,
      name: "Confirm Password",
      type: "password",
      placeholder: "Confirm Your Password",
      
      label: "Confirm Password",
    },
  ];

  const handleChange = (e) => {
    setInput({ ...input, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(input);
  };

  return (
    <div className="register">
      <form className="registeration-form" onSubmit={handleSubmit}>
        <h2>Registration</h2>

        {inputs.map((item) => (
          <div className="form-item" key={item.id}>
            <label className="item-label">{item.label}</label>
            <input
              type={item.type}
              name={item.name}
              placeholder={item.placeholder}
              required="true"
              pattern="^[A-Za-z0-9]{3,16}$"
              className="item-input"
              value={item.username}
              onChange={(e) => handleChange(e)}
            />
            <span>User name must be between 3-16 characters</span>
          </div>
        ))}

        <div className="btn">
          <button type="submit">SUBMIT</button>
        </div>
      </form>
    </div>
  );
};

export default Register;
